            var w = $el.width() - options.padding / 2;
            var h = $el.height() - options.padding / 2;
            $el.empty();
            var r = Math.min(w, h) / 2 - options.padding;
            var color = d3.scale.ordinal().range(
                colorbrewer[options.colorscheme][options.categories]);
            
